            <input type="button" value ="Принять" class="accept-personalData" id="accept-personalData" disabled>
            <script>
                    
                document.getElementById('accept-personalData').onclick = function() {
                    location.href="templates/MainForm.php";
                }

                document.getElementById('checkbox').addEventListener('change', function () {
                    if (this.checked ) {
                        
                        document.getElementById('accept-personalData').disabled = false;
                    } else {
                        document.getElementById('accept-personalData').disabled = true;
                    }
                });


                </script>
                
        </div>
